import axios from "axios";

// base url
const BASE_URL = "http://localhost:3500";

// login

async function login(email, password) {
  try {
    // Replace 'email' and 'password' with the actual field names expected by your login endpoint
    const response = await axios.post(
      `${BASE_URL}/login`,
      {
        email: email,
        password: password,
      },
      {
        headers: {
          "Content-Type": "application/json",
        },
      }
    );

    // If the request was successful, log the response data
    console.log("Login successful:", response.data);

    // You might want to return the response or a specific part of it, such as a token
    return response.data;
  } catch (error) {
    // If the request fails, log or handle the error
    console.error(
      "Login failed:",
      error.response ? error.response.data : error.message
    );

    // You might want to throw the error or return null
  }
}

export { login };
