import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faBell, faUserCircle } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';

const Header = () => {
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const navigate = useNavigate();

  // Function to navigate to the profile page
  const goToProfile = () => {
    navigate('/profile'); // Update '/profile' to the path you have for the profile page
  };

  // Function to navigate to the notifications page or modal
  const goToNotifications = () => {
    navigate('/notifications'); // Update '/notifications' to the path you have for the notifications page or handle opening a modal
  };

  return (
    <header className="w-full bg-white shadow py-4 px-6 flex justify-between items-center">
      {/* Rest of the header content */}
      <div className="flex items-center">
        {/* Search bar */}
        <div className="relative">
          <FontAwesomeIcon icon={faSearch} className="text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
          <input
            type="search"
            className="pl-10 pr-4 py-2 rounded-full bg-gray-200 w-64 focus:outline-none focus:ring-2 focus:ring-blue-600"
            placeholder="Search here..."
          />
        </div>
        {/* Notification icon */}
        <FontAwesomeIcon
          icon={faBell}
          className="text-gray-500 mx-4 cursor-pointer"
          onClick={goToNotifications}
        />
        {/* Profile icon */}
        <FontAwesomeIcon
          icon={faUserCircle}
          className="text-gray-500 cursor-pointer"
          onClick={goToProfile}
        />
        {/* Profile dropdown menu */}
        {isProfileOpen && (
          <div className="absolute right-0 mt-2 py-2 w-48 bg-white rounded-md shadow-xl z-20">
            <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100" onClick={goToProfile}>Your Profile</a>
            {/* ... other dropdown items ... */}
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
