import React from 'react';
import { useNavigate } from 'react-router-dom';

const Header = () => {
  const navigate = useNavigate();

  const goToProfile = () => {
    navigate('/profile'); // Navigates to the Profile component
  };

  const showNotifications = () => {
    // Placeholder function for showing notifications
    // Here you would handle showing a dropdown or navigating to a notifications page
    console.log('Show notifications');
  };

  return (
    <header className="flex justify-between items-center bg-white p-4 shadow z-40 w-full">
      {/* Left side with Logo or title */}
      <div>
        {/* Logo or Title here */}
      </div>

      {/* Right side with icons and search bar */}
      <div className="flex items-center space-x-4">
        {/* Search Bar */}
        <div className="relative">
          <input
            type="search"
            className="pl-10 pr-4 py-2 rounded-full bg-gray-200 w-64 focus:outline-none focus:ring-2 focus:ring-blue-600"
            placeholder="Search here..."
          />
          {/* Search Icon */}
          <svg className="w-6 h-6 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" fill="none" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" viewBox="0 0 24 24" stroke="currentColor">
            <path d="M19 19l-3.5-3.5m4-5.5a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
        </div>

        {/* Notification Icon */}
        <div onClick={showNotifications} className="cursor-pointer">
          <svg className="w-6 h-6 text-gray-400" fill="none" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" viewBox="0 0 24 24" stroke="currentColor">
            <path d="M15 17h5l-1.5-1.5m0 0a4.5 4.5 0 01-9 0m9 0H6" />
            <path d="M13 11V6a4 4 0 10-8 0v5" />
          </svg>
        </div>

        {/* Profile Icon */}
        <div onClick={goToProfile} className="cursor-pointer">
          <svg className="w-6 h-6 text-gray-400" fill="none" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" viewBox="0 0 24 24" stroke="currentColor">
            <path d="M18 20H6a1 1 0 01-1-1v-1a4 4 0 018-0v1a1 1 0 01-1 1z" />
            <path d="M12 11a4 4 0 110-8 4 4 0 010 8z" />
          </svg>
        </div>
      </div>
    </header>
  );
};

export default Header;
