import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faBell, faUser } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';

const Header = () => {
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const navigate = useNavigate();

  const handleProfileClick = () => {
    // Toggle profile dropdown or navigate to profile page
    setIsProfileOpen(!isProfileOpen);
    // If you want to navigate to the profile page instead, uncomment the line below
    // navigate('/profile');
  };

  const handleNotificationsClick = () => {
    // Handle notification click, could open a dropdown or navigate
    console.log('Notifications Clicked');
  };

  return (
    <header className="w-full bg-white shadow py-4 px-6 flex justify-between items-center">
      {/* ... other header content ... */}
      <div className="flex items-center space-x-4">
        {/* ... search bar ... */}
        <button onClick={handleNotificationsClick}>
          <FontAwesomeIcon icon={faBell} className="text-gray-500" />
        </button>
        <button onClick={handleProfileClick}>
          <FontAwesomeIcon icon={faUser} className="text-gray-500" />
        </button>
        {/* Profile dropdown menu */}
        {isProfileOpen && (
          <div className="absolute right-0 mt-2 py-2 w-48 bg-white rounded-md shadow-xl z-20">
            <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">Your Profile</a>
            <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">Settings</a>
            <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">Logout</a>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
