// Sidebar.js
import React from 'react';

const Sidebar = () => {
  return (
    <aside className="w-64 bg-white min-h-screen">
      <div className="flex items-center justify-center pt-6">
        <div className="bg-gray-200 h-10 w-10 rounded-full flex items-center justify-center">
          {/* Replace with logo */}
          <span className="text-xl">tv</span>
        </div>
      </div>
      <nav className="mt-8">
        {/* Sidebar navigation items */}
        {['Dashboard', 'Profile', 'Go Live', 'Notification', 'Statistics'].map((item) => (
          <a
            href="#"
            key={item}
            className="flex items-center px-4 py-2 mt-5 text-gray-600 hover:text-gray-900 hover:bg-gray-100 transition-colors duration-200 transform rounded-md"
          >
            {/* Replace '⚙️' with the actual icon */}
            <span className="text-lg mr-3">⚙️</span>
            {item}
          </a>
        ))}
      </nav>
    </aside>
  );
};

export default Sidebar;
