import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faBell, faUser } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';

const Header = () => {
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const navigate = useNavigate();

  const toggleProfileDropdown = () => {
    setIsProfileOpen(!isProfileOpen);
  };

  const navigateToProfile = () => {
    navigate('/profile');
  };

  return (
    <header className="flex justify-between items-center bg-white p-4 shadow z-40 w-full">
      <div className="flex">
        {/* Rest of the header content */}
      </div>
      <div className="relative">
        {/* Search and icons */}
        <FontAwesomeIcon icon={faSearch} className="text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
        <input
          type="search"
          className="pl-10 pr-4 py-2 rounded-full bg-gray-200 w-64 focus:outline-none focus:ring-2 focus:ring-blue-600"
          placeholder="Search here..."
        />
        <FontAwesomeIcon icon={faBell} className="text-gray-400 mx-4 cursor-pointer" />
        <div className="relative">
          <FontAwesomeIcon icon={faUser} className="text-gray-400 cursor-pointer" onClick={toggleProfileDropdown} />
          {isProfileOpen && (
            <div className="absolute right-0 mt-2 py-2 w-48 bg-white rounded-md shadow-xl z-20">
              <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100" onClick={navigateToProfile}>Your Profile</a>
              <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">Settings</a>
              <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">Logout</a>
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
