// Profile.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUpload } from '@fortawesome/free-solid-svg-icons';
//import { faGoogle } from '@fortawesome/free-brands-svg-icons';

const Profile = () => {
  const [profileData, setProfileData] = useState({
    fullName: 'Sain Bux',
    email: 'sainbuxdev@gmail.com',
    role: 'Workspace Admin',
    cctvCameras: [
      { id: 1, ip: '192.168.1.5:8080' },
      { id: 2, ip: '192.168.1.6:8080' },
      // ... more cameras
    ]
  });

  const navigate = useNavigate();

  const handleCancel = () => {
    navigate('/dashboard');
  };

  const handleSaveChanges = () => {
    // Implement the save logic here
    console.log('Save profile changes');
    navigate('/dashboard');
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow">
      <h2 className="text-xl font-semibold mb-4">Profile</h2>
      {/* ... other profile fields ... */}
      <div className="my-4">
        <h3 className="text-lg font-semibold mb-2">Connected CCTV Cameras</h3>
        <p>{`Total Cameras: ${profileData.cctvCameras.length}`}</p>
        <ul className="list-disc pl-5">
          {profileData.cctvCameras.map((camera) => (
            <li key={camera.id}>{`Camera ${camera.id}: ${camera.ip}`}</li>
          ))}
        </ul>
      </div>
      <div className="flex justify-end mt-4 space-x-2">
        <button className="bg-gray-200 text-gray-700 py-2 px-4 rounded" onClick={handleCancel}>
          Cancel
        </button>
        <button className="bg-purple-600 text-white py-2 px-4 rounded" onClick={handleSaveChanges}>
          Save changes
        </button>
      </div>
    </div>
  );
};

export default Profile;
