import { useNavigate } from 'react-router-dom';
import React, { useState } from 'react';
import Modal from './Modal';

const Incidents = () => {
const [selectedIncident, setSelectedIncident] = useState(null);

  const navigate = useNavigate();
  const goBackToDashboard = () => {
    navigate('/dashboard');
  };
  // Replace with your actual data
  const incidents = [
    { id: 1, src: '1.jpg', alt: 'Forest in fog' },
    { id: 2, src: '2.jpg', alt: 'Stacked logs' },
    { id: 3, src: '3.jpg', alt: 'Blue wooden texture' },// Incidents.js

    // ... your incidents data
  ];

  const handleOpenModal = (incident) => {
    setSelectedIncident(incident);
  };

  // Close the modal
  const handleCloseModal = () => {
    setSelectedIncident(null);
  };
  return (
    <div className="flex-grow p-8">
     <div className="bg-gray-100 p-5">
      <h2 className="text-2xl font-semibold mb-6">Most Recent Fire Incidents</h2>
      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        {incidents.map((incident) => (
          <div key={incident.id} className="aspect-w-1 aspect-h-1">
            <img src={incident.src} alt={incident.alt} className="object-cover rounded-lg" />
          </div>
        ))}
      </div>
      <button 
        className="mb-4 text-indigo-600 hover:text-indigo-800" 
        onClick={goBackToDashboard}>
        ‚Üê Back to Dashboard
      </button>
    </div>
      
    </div>
  );
};

export default Incidents;
