// Profile.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUpload } from '@fortawesome/free-solid-svg-icons';
//import { faGoogle } from '@fortawesome/free-brands-svg-icons';

const Profile = () => {
  const navigate = useNavigate();
  const [profileData, setProfileData] = useState({
    fullName: 'Sain Bux',
    email: 'sainbuxdev@gmail.com',
    role: 'Workspace Admin',
    cctvCameras: [
      { id: 1, ip: '192.168.1.5:8080' },
      { id: 2, ip: '192.168.1.6:8080' },
      // Add more cameras as needed
    ],
  });

  const handleCameraIPChange = (cameraId, newIP) => {
    setProfileData((prevProfileData) => ({
      ...prevProfileData,
      cctvCameras: prevProfileData.cctvCameras.map((camera) =>
        camera.id === cameraId ? { ...camera, ip: newIP } : camera
      ),
    }));
  };

  const handleSaveChanges = () => {
    // Here you would typically send the updated profile data to your server
    console.log('Updated profile data:', profileData);
    navigate('/dashboard');
  };

  const handleCancel = () => {
    navigate('/dashboard');
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow">
      <h2 className="text-xl font-semibold mb-4">Profile</h2>
      {/* ... other profile fields ... */}
      <div className="my-4">
        <h3 className="text-lg font-semibold mb-2">Connected CCTV Cameras</h3>
        {profileData.cctvCameras.map((camera) => (
          <div key={camera.id} className="mb-2">
            <label className="block text-sm font-medium mb-1" htmlFor={`camera-ip-${camera.id}`}>
              Camera {camera.id} IP Address:
            </label>
            <input
              id={`camera-ip-${camera.id}`}
              type="text"
              value={camera.ip}
              onChange={(e) => handleCameraIPChange(camera.id, e.target.value)}
              className="mt-1 p-2 w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
            />
          </div>
        ))}
      </div>
      <div className="flex justify-end mt-4 space-x-2">
        <button onClick={handleCancel} className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded inline-flex items-center">
          Cancel
        </button>
        <button onClick={handleSaveChanges} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded inline-flex items-center">
          Save changes
        </button>
      </div>
    </div>
  );
};

export default Profile;
